CKEDITOR.editorConfig = function( config ) {
	config.enterMode = CKEDITOR.ENTER_P;
	config.shiftEnterMode = CKEDITOR.ENTER_P;
	config.language='ko';			//언어설정 
	config.width='100%'; 
	config.height=300;
	config.filebrowserUploadUrl='/ckeditor/uploadfile.do?type=Files';
	
};
